import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../utils/supabaseClient';

const ResetPassword = () => {
  const [newPassword, setNewPassword] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  const handlePasswordReset = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    // Retrieve the access token from the URL
    const url = new URL(window.location.href);
    const accessToken = url.searchParams.get('access_token');

    if (!accessToken) {
      setError('Invalid or missing token');
      setLoading(false);
      return;
    }

    try {
      // Use Supabase to update the password using the token
      const { error } = await supabase.auth.updateUser({
        access_token: accessToken,  // Pass the access token from the URL
        password: newPassword,      // New password entered by the user
      });

      if (error) {
        setError(error.message);
      } else {
        setSuccess(true);
        alert('Password successfully updated!');
        navigate('/login');  // Redirect to login after successful password reset
      }
    } catch (error) {
      setError('Something went wrong. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reset-password-container">
      <div className="reset-password-box">
        <h2>Reset Your Password</h2>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>Password successfully updated!</p>}
        <form onSubmit={handlePasswordReset}>
          <label>New Password</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
            placeholder="Enter your new password"
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Resetting Password...' : 'Reset Password'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default ResetPassword;
