// src/components/Auth.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import './Auth.css';  // Import the CSS file

const Auth = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [session, setSession] = useState(null);
  const [authMode, setAuthMode] = useState('login'); // 'login', 'signup', or 'resetPassword'

  // eslint-disable-next-line
  const navigate = useNavigate();

  useEffect(() => {
    const { data: authListener } = supabase.auth.onAuthStateChange((event, session) => {
      setSession(session);
      if (session) {
        navigate('/jobboard');
      }
    });

    // Return cleanup function to unsubscribe from the listener
    return () => {
      if (authListener) {
        authListener.unsubscribe();
      }
    };
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const { error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) {
      setError(error.message);
    } else {
      navigate('/dashboard');
    }
    setLoading(false);
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const { error } = await supabase.auth.signUp({
      email,
      password,
    });

    if (error) {
      setError(error.message);
    } else {
      alert('Sign-up successful! Check your email for verification.');
      setAuthMode('login');
    }
    setLoading(false);
  };

  const handleResetPassword = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const { error } = await supabase.auth.resetPasswordForEmail(email);

    if (error) {
      setError(error.message);
    } else {
      alert('Password reset link has been sent to your email!');
      setAuthMode('login');
    }
    setLoading(false);
  };

  const renderForm = () => {
    switch (authMode) {
      case 'login':
        return (
          <form onSubmit={handleLogin}>
            <h2>Login</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" disabled={loading}>
              {loading ? 'Logging in...' : 'Login'}
            </button>
            <p>
              Don't have an account?{' '}
              <span onClick={() => setAuthMode('signup')}>Sign up here</span>
            </p>
            <p>
              Forgot password?{' '}
              <span onClick={() => setAuthMode('resetPassword')}>Reset here</span>
            </p>
          </form>
        );
      case 'signup':
        return (
          <form onSubmit={handleSignup}>
            <h2>Sign Up</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" disabled={loading}>
              {loading ? 'Signing up...' : 'Sign Up'}
            </button>
            <p>
              Already have an account?{' '}
              <span onClick={() => setAuthMode('login')}>Login here</span>
            </p>
          </form>
        );
      case 'resetPassword':
        return (
          <form onSubmit={handleResetPassword}>
            <h2>Reset Password</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <button type="submit" disabled={loading}>
              {loading ? 'Sending reset link...' : 'Send Reset Link'}
            </button>
            <p>
              Back to{' '}
              <span onClick={() => setAuthMode('login')}>Login</span>
            </p>
          </form>
        );
      default:
        return null;
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        {session ? (
          <div>
            <h2>Welcome, {session.user.email}</h2>
            <button
              onClick={async () => {
                await supabase.auth.signOut();
                setSession(null);
                navigate('/');
              }}
            >
              Logout
            </button>
          </div>
        ) : (
          renderForm()
        )}
      </div>
    </div>
  );
};

export default Auth;
