import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient'; // Ensure supabaseClient is properly set up

const Auth = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [session, setSession] = useState(null);

  const navigate = useNavigate();

  // Handle session state
  useEffect(() => {
    const { data: authListener } = supabase.auth.onAuthStateChange((event, session) => {
      setSession(session); // Update session state
      if (session) {
        navigate('/jobboard'); // Redirect if logged in
      }
    });

    // Cleanup subscription on unmount
    return () => {
      authListener?.subscription.unsubscribe();
    };
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const { data, error } = await supabase.auth.signInWithPassword({
      email: email,
      password: password,
    });

    if (error) {
      console.error('Login Error:', error.message);
      setError(error.message);
      setLoading(false);
      return;
    }

    if (data?.user) {
      console.log('Login Successful:', data.user);
      setSession(data.session); // Store the session if necessary
      navigate('/jobboard'); // Redirect to dashboard
    }

    setLoading(false);
  };

  const handleLogout = async () => {
    const { error } = await supabase.auth.signOut();
    if (error) {
      console.error('Error logging out:', error.message);
    } else {
      setSession(null);
      navigate('/'); // Redirect to home after logout
    }
  };

  // Handle Sign Up
  const handleSignup = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const { error } = await supabase.auth.signUp({
      email,
      password,
    });

    if (error) {
      setError(error.message);
    } else {
      alert('Sign-up successful! Check your email for verification.');
      setAuthMode('login'); // After signup, switch back to login mode
    }
    setLoading(false);
  };

  // Handle Reset Password
  const handleResetPassword = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const { error } = await supabase.auth.resetPasswordForEmail(email);

    if (error) {
      setError(error.message);
    } else {
      alert('Password reset link has been sent to your email!');
      setAuthMode('login'); // After sending reset email, switch back to login mode
    }
    setLoading(false);
  };

  const renderForm = () => {
    switch (authMode) {
      case 'login':
        return (
          <form onSubmit={handleLogin}>
            <h2>Login</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" disabled={loading}>
              {loading ? 'Logging in...' : 'Login'}
            </button>
            <div>
              <p onClick={() => setAuthMode('signup')} style={{ cursor: 'pointer', color: 'blue' }}>
                Don't have an account? Sign up here
              </p>
              <p onClick={() => setAuthMode('resetPassword')} style={{ cursor: 'pointer', color: 'blue' }}>
                Forgot password? Reset here
              </p>
            </div>
          </form>
        );
      case 'signup':
        return (
          <form onSubmit={handleSignup}>
            <h2>Sign Up</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" disabled={loading}>
              {loading ? 'Signing up...' : 'Sign Up'}
            </button>
            <div>
              <p onClick={() => setAuthMode('login')} style={{ cursor: 'pointer', color: 'blue' }}>
                Already have an account? Login here
              </p>
            </div>
          </form>
        );
      case 'resetPassword':
        return (
          <form onSubmit={handleResetPassword}>
            <h2>Reset Password</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <button type="submit" disabled={loading}>
              {loading ? 'Sending reset link...' : 'Send Reset Link'}
            </button>
            <div>
              <p onClick={() => setAuthMode('login')} style={{ cursor: 'pointer', color: 'blue' }}>
                Back to Login
              </p>
            </div>
          </form>
        );
      default:
        return null;
    }
  };


  return (
    <div className="auth-container">
      {session ? (
        <div>
          <h2>Welcome, {session.user.email}</h2>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <form onSubmit={handleLogin} className="auth-form">
          <h2>Login</h2>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <div>
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
      )}
    </div>
  );
};

export default Auth;
